    if (A.dep[u] - 1 > B.calc(t, u))
        return false;
    for (int i = 0; i < (int)A.g[u].size(); ++i) {
        int v = A.g[u][i];
        if (v == pre)
            continue;
        if (B.calc(u, v) > 2|| DFS(v, u))
            return true;
    }
    return false;
}

void find(int u, int pre) {
    if (A.dep[u] - 1 > B.calc(t, u))
        return ;
    ans = max(ans, B.calc(t, u) * 2);
    for (int i = 0; i < (int)A.g[u].size(); ++i) {
        int v = A.g[u][i];
        if (v == pre)
            continue;
        find(v, u);
    }
}

