int pr[maxn];
int used[maxn];

int res;

vector<int> st;
int l;

void dfs(int v, int v1, int pass) {
  assert(v1 == st[l] && v == st.back());
  assert(v != v1);
  used[v] = 1;
  res = max(res, 2 * (sz(st) - l - 1 + pass));

  for (int nv : es[0][v]) {
    if (used[nv]) {
      continue;
    }
    int s = v, t = nv;
    int x = -1;
    bool ok = 0;
    if (pr[s] == pr[t]) {
      ok = 1;
      x = pr[s];
    }
    for (int it = 0; !ok && it < 2; ++it) {
      if (pr[s] == t) {
        ok = 1;
        x = -1;
      } else {
        if (pr[s] != -1 && pr[pr[s]] == t) {
          ok = 1;
          x = pr[s];
        }
      }
      swap(s, t);
    }

    if (!ok) {
      res = -1;
      break;
    }

    if (x == v1 || nv == v1) {
      continue;
    }
    int vs[3] = {v, x, nv};
    int k = 3;
    if (x == -1) {
      vs[1] = vs[2];
      --k;
    }
    int cnt = 0;
    while (cnt < k && vs[cnt] == st[sz(st) - cnt - 1]) {
      ++cnt;
    }
    assert(cnt);
    st.resize(sz(st) - cnt + 1);

    for (int i = cnt; i < k; ++i) {
      st.pb(vs[i]);
    }

    ++l;
    if (l + 1 < sz(st)) {
      dfs(st.back(), st[l], pass + 1);
      if (res == -1) {
        break;
      }
    }
    --l;

    st.resize(sz(st) - (k - cnt));
    for (int i = cnt - 2; i >= 0; --i) {
      st.pb(vs[i]);
    }
  }
}

bool dfs2(int v, int p, int need) {
  pr[v] = p;
  bool res = 0;
  if (need == v) {
    st.clear();
    res = 1;
  }
  for (int u : es[1][v]) {
    if (u == p) {
      continue;
    }
    if (dfs2(u, v, need)) {
      res = 1;
    }
  }

  if (res) {
    st.pb(v);
  }

  return res;
}

void solve() {
  for (int i = 0; i < n; ++i) {
    used[i] = 0;
  }

  assert(dfs2(vs[0], -1, vs[1]));

  res = 0;
  dfs(vs[0], vs[1], 0);
  printf("%d\n", res);
}

