bool bad[maxn],mk[maxn];
void dfs(ll v){for(auto u:g[v])if(u!=par[v])par[u]=v,h[u]=h[v]+1,dfs(u);}
void df2(ll v,ll d=0){
	mk[v]=1;
	if(bad[v] && h[v]-d>0){cout<<-1;exit(0);}
	ans=max(ans,2*h[v]);
	for(auto u:ex[v]) df2(u,d+1);
}

int main(){
	ios_base::sync_with_stdio(0),cin.tie(0),cout.tie(0);

	cin>>n>>X>>Y;
	vector<pll> E;
	for(int i=1;i<n;i++){
		ll v,u; cin>>v>>u;
		E.pb({v,u});
	}
	for(int i=1;i<n;i++){
		ll v,u; cin>>v>>u;
		g[v].pb(u), g[u].pb(v);
	}
	dfs(Y);
	for(auto e:E){
		ll v=e.F, u=e.S;
		if(par[v]==par[u]) continue;
		if(par[par[v]]==u || par[v]==u) ex[u].pb(v);
		else if(par[par[u]]==v || par[u]==v) ex[v].pb(u);
		else bad[v]=bad[u]=1;
	}
	df2(X);
	cout<<ans;

	return 0;
}

