	}
	FOR(i,N-1) {
		cin>>x>>y;
		E[x].push_back(y);
		E[y].push_back(x);
	}
	P[0][Y]=Y;
	dfs(Y);
	FOR(i,19) FOR(x,N) P[i+1][x+1]=P[i][P[i][x+1]];
	FOR(i,N) D2[i+1]=10101010;

	int ret=0;
	queue<int> Q;
	D2[X]=0;
	Q.push(X);
	while(Q.size()) {
		x = Q.front();
		Q.pop();
		ret=max(ret,D[x]);
		if(D2[x]>=D[x]) continue;
		FORR(e,E2[x]) {
			if(dist(x,e)>=3) return _P("-1\n");
			if(D2[e]>D2[x]+1) D2[e]=D2[x]+1, Q.push(e);
		}
	}

	cout<<ret*2<<endl;

}


