#include <bits/stdc++.h>
using namespace std;
constexpr int MAXN = 2e5 + 10;
struct edge {
    static int ptr;
    int to, next;
} e[MAXN * 4];
int edge::ptr = 0;

int G1[MAXN], G2[MAXN], depth2[MAXN], parent2[MAXN];
void dfs2(int o, int p, int d) {
    parent2[o] = p, depth2[o] = d;
    for (int i = G2[o]; i; i = e[i].next) {
        if (e[i].to == p) continue;
        dfs2(e[i].to, o, d + 1);
    }
}
constexpr int INF = 1e9;
int baluteshih_orz(int x, int y) {
    if (depth2[x] < depth2[y]) swap(x, y);
    if (x == y) return 0;
    if (parent2[x] == y) return 1;
    if (parent2[x] == parent2[y]) return 2;
    if (parent2[parent2[x]] == y) return 2;
    return INF;
}
int dfs1(int o, int p, int d2) {
    if (depth2[o] <= d2) return d2;
    int ret = depth2[o];
    for (int i = G1[o]; i; i = e[i].next) {
        int to = e[i].to;
        if (to == p) continue;
        if (baluteshih_orz(o, to) == INF) return INF;
        ret = max(ret, dfs1(to, o, d2 + 1));
    }
    return ret;
}

