    for (int i = 1, u, v; i < N; i++) {
        cin >> u >> v;
        e[++edge::ptr] = {v, G1[u]}, G1[u] = edge::ptr;
        e[++edge::ptr] = {u, G1[v]}, G1[v] = edge::ptr;
    }
    for (int i = 1, u, v; i < N; i++) {
        cin >> u >> v;
        e[++edge::ptr] = {v, G2[u]}, G2[u] = edge::ptr;
        e[++edge::ptr] = {u, G2[v]}, G2[v] = edge::ptr;
    }
    dfs2(Y, 0, 0);
    int ans = dfs1(X, 0, 0);
    cout << (ans == INF ? -1 : 2 * ans) << endl;
    return 0;
}

