bool dis3(ll x,ll y)
{
    if (h[x]>h[y]) swap(x,y);
    if (h[x]==h[y])
        return par[x]==par[y];
    else if (h[y]-h[x]==1)
        return par[y]==x;
    else return par[par[y]]==x;
}

