const int N = 200001;

int n, x, y, a[N], b[N], depth[N], parent[N], escape[N], dist[N];
std::vector<int> tree[N], graph[N];

void prepare(int p, int u) {
  depth[u] = depth[p] + 1, parent[u] = p;
  for (int v : tree[u]) {
    if (v != p) {
      prepare(u, v);
    }
  }
}

int treedist(int a, int b) {
  int d = 0;
  while (d < 3 && a != b) {
    int &u = depth[a] > depth[b] ? a : b;
    d++, u = parent[u];
  }
  return d;
}

int main() {
  scanf("%d%d%d", &n, &x, &y);
  for (int i = 0; i < n - 1; ++i) {
    scanf("%d%d", a + i, b + i);
  }
  for (int i = 0, c, d; i < n - 1; ++i) {
    scanf("%d%d", &c, &d);
    tree[c].push_back(d);
    tree[d].push_back(c);
  }
  depth[0] = -1;
  prepare(0, y);
  for (int i = 0; i < n - 1; ++i) {
    if (treedist(a[i], b[i]) == 3) {
      escape[a[i]] = escape[b[i]] = true;
    } else {
      graph[a[i]].push_back(b[i]);
      graph[b[i]].push_back(a[i]);
    }
  }
  std::vector<int> queue;
  queue.push_back(x);
  memset(dist, -1, sizeof(dist));
  dist[x] = 0;
  for (int head = 0; head < static_cast<int>(queue.size()); ++head) {
    int u = queue[head];
    for (int v : graph[u]) {
      if (dist[v] == -1) {
        dist[v] = dist[u] + 1;
        queue.push_back(v);
      }
    }
  }
  int result = 0;
  for (int i = 1; i <= n; ++i) {
    if (~dist[i] && dist[i] < depth[i]) {
      result = std::max(result, depth[i] * 2);
    }
    if (escape[i] && ~dist[i] && dist[i] < depth[i]) {
      result = -1;
      break;
    }
  }
  printf("%d\n", result);
}

#include <bits/stdc++.h>

#define MAXN 200005

using namespace std;

int n, s, t, ans;

struct graph {
    vector<int> g[MAXN];
    int fa[MAXN][19], dep[MAXN];

    void connect(int u, int v) {
        g[u].push_back(v);
        g[v].push_back(u);
    }

    void build(int u = s, int pre = 0) {
        fa[u][0] = pre;
        dep[u] = dep[pre] + 1;
        for (int i = 1; i <= 18; ++i)
            fa[u][i] = fa[fa[u][i - 1]][i - 1];
        for (int i = 0; i < (int)g[u].size(); ++i) {
            int v = g[u][i];
            if (v == pre)
                continue;
            build(v, u);
        }
    }

    int query(int u, int v) {
        if (dep[u] < dep[v])
            swap(u, v);
        for (int i = 18; i >= 0; --i)
            if (dep[fa[u][i]] >= dep[v])
                u = fa[u][i];
        if (u == v)
            return u;
        for (int i = 18; i >= 0; --i)
            if (fa[u][i] != fa[v][i]) {
                u = fa[u][i];
                v = fa[v][i];
            }
        return fa[u][0];
    }

    int calc(int u, int v) {
        return dep[u] + dep[v] - dep[query(u, v)] * 2;
    }
}A, B;


int read() {
    char c = getchar();
    int x = 0;
    while (!isdigit(c))
        c = getchar();
    while (isdigit(c)) {
        x = (x << 3) + (x << 1) + c - '0';
        c = getchar();
    }
    return x;
}

bool DFS(int u, int pre) {
    if (A.dep[u] - 1 >= B.calc(t, u))
        return false;
    for (int i = 0; i < (int)A.g[u].size(); ++i) {
        int v = A.g[u][i];
        if (v == pre)
            continue;
        if (B.calc(u, v) > 2 || DFS(v, u))
            return true;
    }
    return false;
}

void find(int u, int pre) {
    if (A.dep[u] - 1 > B.calc(t, u))
        return ;
    ans = max(ans, B.calc(t, u) * 2);
    for (int i = 0; i < (int)A.g[u].size(); ++i) {
        int v = A.g[u][i];
        if (v == pre)
            continue;
        find(v, u);
    }
}

int main() {
//    freopen("giri4.in", "r", stdin);
    n = read();
    s = read();
    t = read();
    for (int i = 1; i < n; ++i)
        A.connect(read(), read());
    for (int i = 1; i < n; ++i)
        B.connect(read(), read());
    A.build();
    B.build();
    bool isinfinity = DFS(s, 0);
    if (isinfinity)
        puts("-1");
    else {
        find(s, 0);
        printf("%d\n", ans);
    }
}

#include <bits/stdc++.h>
using namespace std;
#define N 200001
#define M 21
struct {
    vector<int> e[N];
    int fa[N][M], d[N];
    void dfs(int u) {
        for (auto v : e[u]) {
            if (v == fa[u][0]) continue;
            fa[v][0] = u, d[v] = d[u] + 1;
            for (int i = 1; i < M; i++) fa[v][i] = fa[fa[v][i - 1]][i - 1];
            dfs(v);
        }
    }
    void init(int n) {
        for (int i = 1, u, v; i < n; i++) {
            scanf("%d%d", &u, &v);
            e[u].push_back(v);
            e[v].push_back(u);
        }
    }
    int lca(int u, int v) {
        if (d[u] < d[v]) swap(u, v);
        for (int i = M - 1; ~i; i--)
            if (d[fa[u][i]] >= d[v]) u = fa[u][i];
        if (u == v) return u;
        for (int i = M - 1; ~i; i--)
            if (fa[u][i] != fa[v][i]) u = fa[u][i], v = fa[v][i];
        return fa[u][0];
    }
} ex, ey;
int main() {
    int n, addx, addy;
    scanf("%d%d%d", &n, &addx, &addy);
    ex.init(n);
    ey.init(n);
    ex.dfs(addx);
    ey.dfs(addy);
    queue<int> que;
    que.push(addx);
    int ans = 0;
    while (!que.empty()) {
        int u = que.front();
        que.pop();
        int f = ex.fa[u][0], tmp = ey.lca(u, f);
        if (ey.d[u] + ey.d[f] - 2 * ey.d[tmp] > 2) {
            puts("-1");
            return 0;
        }
        ans = max(ans, ey.d[u] << 1);
        if (ex.d[u] >= ey.d[u]) continue;
        for (auto v : ex.e[u])
            if (v != f) que.push(v);
    }
    printf("%d\n", ans);
    return 0;
}

