const int TREE_MAXV=300000+5;
const int LCA_UP=20;
struct edge{int v,next;}e[TREE_MAXV*2];int g[TREE_MAXV],etot;
int qu[TREE_MAXV],pr[LCA_UP][TREE_MAXV],pre[TREE_MAXV],h[TREE_MAXV];
int n;
void ae(int u,int v){
	e[etot].v=v;
	e[etot].next=g[u];g[u]=etot++;
}
void bfs(int rt){
	int p=0,q=0;
	pr[0][rt]=pre[rt]=0;
	h[rt]=0;
	qu[q++]=rt;
	while(p!=q){
		int u=qu[p++];
		for (int i=g[u];~i;i=e[i].next)if(e[i].v!=pre[u]){
			pr[0][e[i].v]=pre[e[i].v]=u;
			qu[q++]=e[i].v;
			h[e[i].v]=h[u]+1;
		}
	}
	for (int i=1;i<LCA_UP;i++)
		for (int u=1;u<=n;u++){
			pr[i][u]=pr[i-1][pr[i-1][u]];
		}
}
int lca(int u,int v){
	if(h[u]>h[v])swap(u,v);
	for (int i=0;h[v]-h[u];i++)if((1<<i)&(h[v]-h[u])){
		v=pr[i][v];
	}
	if(u==v)return u;
	for (int i=LCA_UP-1;i>=0;i--)if(pr[i][u]!=pr[i][v]){
		v=pr[i][v];
		u=pr[i][u];
	}
	return pr[0][u];
}
void tree_init(){
	static bool ini=0;
	if(!ini){
		ini=1;
		memset(g,-1,sizeof(g));
	}else{
		for (int i=0;i<=n;i++)g[i]=-1;
	}
	etot=0;
}

