		if(!czy[blue[x][i]])
			calculate_special (blue[x][i], x);
	}
	join (x, y);
}

int answer (int x, int y)
{
	czy1 [x] = true;
	if (special[x] && bdistance [x] > y)
		return inf;
	if (bdistance [x] <= y)
		return y;
	int odp = bdistance [x];
	int s = (int)red[x].size();
	for (int i = 0; i<s; ++i)
	{
		if (!czy1[red[x][i]])
		{
			odp = max(answer (red[x][i], y + 1), odp);
		}
	}
	return odp;	
}


