struct state {
        int v, step;
        state(int v, int step) : v(v), step(step) { }
};

vector<int> gx[202020];
int depth_x[202020];

vector<int> gy[202020];
int depth_y[202020];
int parent[30][202020];

void dfs_lca(int v, int p, int d) {
        parent[0][v] = p;
        depth_y[v] = d;
        for (auto i : gy[v]) if (i != p) dfs_lca(i, v, d + 1);
}
void init_lca(int s, int V) {
        dfs_lca(s, -1, 0);
        for (int k = 0; k < 30 - 1; k ++) {
                for (int i = 0; i < V; i ++) {
                        if (parent[k][i] < 0) parent[k + 1][i] = -1;
                        else parent[k + 1][i] = parent[k][parent[k][i]];
                }
        }
}
int lca(int u, int v) { 
        if (depth_y[u] > depth_y[v]) swap(u, v);
        for (int k = 0; k < 30; k ++) {
                if ((depth_y[v] - depth_y[u]) >> k & 1) { 
                        v = parent[k][v];
                }
        }
        if (u == v) return u;
        for (int k = 30 - 1; k >= 0; k --) {
                if (parent[k][u] != parent[k][v]) {
                        u = parent[k][u];
                        v = parent[k][v];
                }
        }
        return parent[0][u];
}
int dist(int u, int v) {
        return depth_y[u] + depth_y[v] - depth_y[lca(u, v)] * 2;
}

