void addedge(int u,int v,int col){
	e[col][++cnt[col]].v=v;
	e[col][cnt[col]].Next=head[col][u];
	head[col][u]=cnt[col];
}
void dfs(int p,int fa,int type){
	if(type) rep(i,1,20) f[p][i]=f[f[p][i-1]][i-1];
	for(int ne=head[type][p];ne;ne=e[type][ne].Next){
		if(e[type][ne].v==fa) continue;
		if(type) f[e[type][ne].v][0]=p;
		dep[type][e[type][ne].v]=dep[type][p]+1;
		dfs(e[type][ne].v,p,type);
	}
}
int LCA(int u,int v){
	if(dep[1][u]<dep[1][v]) swap(u,v);
	per(i,20,0) if(dep[1][f[u][i]]>=dep[1][v]) u=f[u][i];
	if(u==v) return u;
	per(i,20,0) if(f[u][i]!=f[v][i]) u=f[u][i],v=f[v][i];
	return f[u][0]; 
}
void solve(int p,int fa){
	if(dep[0][p]>=dep[1][p]) return ;
	if(ans==-1) return ;
	ans=max(ans,(dep[1][p]-1)*2);
	for(int ne=head[0][p];ne;ne=e[0][ne].Next){
		if(e[0][ne].v==fa) continue;
		if(dep[1][p]+dep[1][e[0][ne].v]-2*dep[1][LCA(p,e[0][ne].v)]>2) return (void)(ans=-1);
		solve(e[0][ne].v,p);
	}
}
