#include <bits/stdc++.h>

using namespace std;

const int NMAX = 200000 + 5;

int N;
vector <int> tomTree[NMAX];
vector <int> jerryTree[NMAX];

int tomCnt;
int tomH[NMAX];
int tomFather[NMAX];
int tomFirst[NMAX];
int tomLast[NMAX];

void dfsPrepareTomTree(int node) {
    tomFirst[node] = ++tomCnt;
    if (tomFather[node])
        tomTree[node].erase(find(tomTree[node].begin(), tomTree[node].end(), tomFather[node]));
    for (auto it: tomTree[node]) {
        tomH[it] = 1 + tomH[node];
        tomFather[it] = node;
        dfsPrepareTomTree(it);  
    }
    tomLast[node] = tomCnt;
}

int moveCloser(int t, int j) {
    if (t == j) // No need to move
        return t;
    else if (tomFirst[t] <= tomFirst[j] && tomFirst[j] <= tomLast[t]) { // Move in a son
        int st = 1, dr = tomTree[t].size() - 1;
        int ans = 0;
        while (st <= dr) {
            int mid = (st + dr) >> 1;
            if (tomFirst[tomTree[t][mid]] <= tomFirst[j])
                ans = mid, st = mid + 1;
            else
                dr = mid - 1;
        }
        return tomTree[t][ans];
    }
    else // Move in the father
        return tomFather[t];
}

int getDist(int a, int b) {
    int ans = 0;
    while (a != b) {
        if (tomH[a] < tomH[b])
            swap(a, b);
        ++ ans;
        if (ans == 3)
            break;
        a = tomFather[a];
    }
    return ans;
}

const int INF = 2E9 + 256;
bool easyWinJerry[NMAX];

bool jerryVis[NMAX];
int play(int t, int j) { // Jerry moves
    if (t == j)
        return 0;
    if (easyWinJerry[j])
        return INF;
    jerryVis[j] = true;

    int longest = 2 + play(moveCloser(t, j), j);
    for (auto it: jerryTree[j])
        if (!jerryVis[it])
            longest = max(longest, 2 + play(moveCloser(t, it), it));
    jerryVis[j] = false;
    return longest;
}

int main() {
    //freopen("data.in", "r", stdin);
    ios_base :: sync_with_stdio(false);

    int t, j;
    cin >> N >> j >> t;
    for (int i = 1; i < N; ++ i) {
        int a, b;
        cin >> a >> b;
        jerryTree[a].push_back(b);
        jerryTree[b].push_back(a);
    }
    for (int i = 1; i < N; ++ i) {
        int a, b;
        cin >> a >> b;
        tomTree[a].push_back(b);
        tomTree[b].push_back(a);
    }
    dfsPrepareTomTree(t);

    for (int i = 1; i <= N; ++ i)
        for (auto it: jerryTree[i]) {
            int d = getDist(i, it);
            if (d >= 3)
                easyWinJerry[i] = easyWinJerry[it] = true;
        }

    int ans = play(t, j);
    if (ans >= INF)
        cout << "-1\n";
    else
        cout << ans << '\n';
    return 0;
}

