			if(dis[R]==-1)
			{
				dis[R]=dis[x]+1;
				if(dis[R]<dep[R]) Q.push(R);
			}
		}
	}
}
void bfs2()
{
	memset(dep,-1,sizeof(dep));
	Q.push(p2);dep[p2]=0;
	while(!Q.empty())
	{
		int x=Q.front();Q.pop();
		for(int i=B.head[x];i;i=B.a[i].next)
		{
			int R=B.a[i].to;
			if(dep[R]==-1) dep[R]=dep[x]+1,fr[R]=x,Q.push(R);
		}
	}
}
int Dis(int x,int y)
{
	if(dep[x]<dep[y]) swap(x,y);
	if(fr[x]==fr[y]||x==y||fr[x]==y) return 0;
	if(fr[fr[x]]==y) return 0;
	return 1;
}
