std::vector<int> adj_r[200005], adj_b[200005];
int pa[200005], dep[200005], ans;
void init(int u, int fa = -1)
{
	pa[u] = fa;
	for (int v : adj_b[u])
	{
		if (v != fa)
		{
			dep[v] = dep[u] + 1;
			init(v, u);
		}
	}
}
inline bool chk(int u, int v)
{
	if (dep[u] < dep[v])
		std::swap(u, v);
	for (int i = 0; i < 2 && u != v; i++)
	{
		if (dep[u] < dep[v])
			v = pa[v];
		else
			u = pa[u];
	}
	return u == v;
}
void dfs(int u, int len = 0, int fa = -1)
{
	ans = std::max(ans, dep[u]);
	if (dep[u] <= len)
		return;
	for (int v : adj_r[u])
	{
		if (v == fa)
			continue;
		if (!chk(u, v))
			throw false;
		dfs(v, len + 1, u);
	}
}
