	f[x][0] = fa[x];
	for (int i = 1; (1 << i) <= dep[x]; ++i)
		f[x][i] = f[f[x][i - 1]][i - 1];
	for (int v : Tb[x]) if (v != fa[x]) {
		fa[v] = x, dep[v] = dep[x] + 1, dfs(v);
	}
}

int lca(int x, int y) {
	if (dep[x] < dep[y]) swap(x, y);
	iter_d0(i, 20) if (dep[f[x][i]] >= dep[y]) x = f[x][i];
	if (x == y) return x;
	iter_d0(i, 20) if (f[x][i] != f[y][i]) x = f[x][i], y = f[y][i];
	return fa[x];
}

int dist(int x, int y) {
	return dep[x] + dep[y] - 2 * dep[lca(x, y)];
}

