void add(int u,int v) {
	En++; nxt[En]=fst[u]; fst[u]=En; to[En]=v;
}
void dfs(int x,int fa,int step,int *A)
{
	int j,v;
	if (mode==1 && x==y) return ;
	A[x]=step; Fa[x]=fa;
	edge(j)
	{
		v=to[j];
		if (v==fa) continue;
		dfs(v,x,step+1,A);
	}
}
void Init()
{
	scanf("%d%d%d",&n,&x,&y);
	rep(i,1,n-1)
	{
		scanf("%d%d",&u,&v);
		add(u,v); add(v,u);
		U[i]=u; V[i]=v;
	}
	rep(i,1,n) A[i]=B[i]=n+1;
	mode=1; dfs(x,x,0,A);
	memset(fst,0,sizeof(fst));
	En=0;
	rep(i,1,n-1)
	{
		scanf("%d%d",&u,&v);
		add(u,v); add(v,u);		
	}
	mode=2; dfs(y,y,0,B);
}
void Solve()
{
	rep(i,1,n-1)
	{
		if (Fa[U[i]]==V[i] || Fa[V[i]]==U[i]) continue;
		if (Fa[U[i]]==Fa[V[i]] || Fa[Fa[U[i]]]==V[i] || Fa[Fa[V[i]]]==U[i]) continue;
		if (A[U[i]]<B[U[i]] || A[V[i]]<B[V[i]])
		{
			printf("-1\n");
			return ;
		}
	}
	rep(i,1,n)
		if (A[i]<B[i])
			ans=max(ans,B[i]);
	ans*=2;
	printf("%d\n",ans);
}
int main()
{
