{
    ans = max(ans, d[x]<<1); 
    for(int i = ha[x]; i; i = edge[i].next)
    {
        int y = edge[i].to; 
        if(dis(x, y) > 2) cir = 1;    
        if(y == fa||dep+1 >= d[y]) continue;
        escape(y, x, dep+1);
    }
}
