template<class T = ll> constexpr T TEN(int n) {return (n==0)?1:10*TEN<T>(n-1);}

const int MN = 200200;
int n;
vector<int> g[MN], ga[MN];

int dps[MN], par[MN];
void dfs(int p, int b, int dp = 0) {
    dps[p] = dp;
    par[p] = b;
    for (int d: g[p]) {
        if (d == b) continue;
        dfs(d, p, dp+1);
    }
}

int dist3(int a, int b) {
    if (dps[a] < dps[b]) swap(a, b);
    if (a == b) return false; // dist 0
    if (par[a] == b) return false; // dist 1
    if (par[a] == par[b]) return false; // dist 2(a)
    if (par[a] != -1 && par[par[a]] == b) return false; // dist 2(b)
    return true;
}

int calc(int p, int b, int dp = 0) {
    if (dps[p] <= dp) return 0;
    int ans = 2*(dps[p]-dp); // pass
    for (int d: ga[p]) {
        if (d == b) continue;
        if (dist3(d, p)) {
            return TEN(9); // dist 3 jump
        }
        ans = max(ans, 2+calc(d, p, dp+1));
    }
    return ans;
}

int main() {
    int x, y;
    scanf("%d %d %d", &n, &x, &y); x--; y--;
    for (int i = 0; i < n-1; i++) {
        int a, b;
        scanf("%d %d", &a, &b); a--; b--;
        ga[a].push_back(b);
        ga[b].push_back(a);
    }
    for (int i = 0; i < n-1; i++) {
        int a, b;
        scanf("%d %d", &a, &b); a--; b--;
        g[a].push_back(b);
        g[b].push_back(a);
    }
    dfs(y, -1);
    int ans = calc(x, -1);
    if (ans > TEN(9)/2) ans = -1;
    printf("%d\n", ans);
    return 0;
}

