int F()
{
	char ch;
	int x, a;
	while (ch = getchar(), (ch < '0' || ch > '9') && ch != '-');
	if (ch == '-') ch = getchar(), a = -1;
	else a = 1;
	x = ch - '0';
	while (ch = getchar(), ch >= '0' && ch <= '9')
		x = (x << 1) + (x << 3) + ch - '0';
	return a * x;
}

const int N = 2e5 + 10;

int n;

VI A[N], B[N];

int fa[N], in[N], out[N], dep[N], dfs_clock;

void dfsB(int u)
{
	in[u] = ++dfs_clock;
	for (auto v : B[u])
	{
		if (v == fa[u]) continue;
		fa[v] = u;
		dep[v] = dep[u] + 1;
		dfsB(v);
	}
	out[u] = dfs_clock;
}

bool check(int u, int v)
{
	if (in[u] <= in[v] && in[v] <= out[u])
	{
		if (fa[v] == u) return 1;
		if (fa[fa[v]] == u) return 1;
		return 0;
 	}
 	if (in[v] <= in[u] && in[u] <= out[v])
	{
		if (fa[u] == v) return 1;
		if (fa[fa[u]] == v) return 1;
		return 0;
 	}
 	if (fa[u] == fa[v]) return 1;
 	return 0;
}

