int lca(int a, int b){
  if(distH[a] > distH[b]){
    swap(a, b);
  }
  for(int i = 19; i >= 0; i--){
    if((distH[b] - distH[a]) & PW(i)){
      b = par[b][i];
    }
  }
  if(a == b){
    return a;
  }
  for(int i = 19; i >= 0; i--){
    if(par[a][i] != par[b][i]){
      a = par[a][i];
      b = par[b][i];
    }
  }
  assert(a != b);
  assert(par[a][0] == par[b][0]);
  return par[a][0];
}

int dist(int a, int b){
  return distH[a] + distH[b] - 2 * distH[lca(a, b)];
}

