int calc(int x,int y){
	if(dist2[x] > dist2[y]) swap(x,y);
	rep(i,18){
		if((((dist2[y]-dist2[x])>>i)&1)){
			y = lca[y][i];
		}
	}
	for(int i=17;i>=0;i--){
		if(lca[x][i] != lca[y][i]){
			x = lca[x][i];
			y = lca[y][i];
		}
	}
	return (x==y?x:lca[x][0]);
}
bool fe;
int go(int a,int b){
	int c = calc(a,b);
	if(a == c){
		for(int i=0;i<18;i++){
			if((((dist2[b]-dist2[a]-1)>>i)&1)){
				b = lca[b][i];
			}
		}
		return b;
	}
	else{
		return lca[a][0];
	}
}
