		//cout << v << " " << edge[v][i] << " " << " " << calc(v,edge[v][i]) << D << endl;
		if(D>=3 && lca[edge[v][i]][0] != dp[v].sc && lca[dp[v].sc][0] != edge[v][i] && edge[v][i] != dp[v].sc){

			fe = 1;
		}
		else {
			D = dist2[edge[v][i]]+dist2[dp[v].sc]-2*dist2[calc(edge[v][i],dp[v].sc)];
			if(D<=1){
				dp[edge[v][i]] = max(dp[edge[v][i]],mp(0,edge[v][i]));
			}
			else{
				dp[edge[v][i]] = max(dp[edge[v][i]],mp(D-1,go(dp[v].sc,edge[v][i])));
				dfs(edge[v][i],v,d+1);

			}
		}
	}
}
