struct Graph {
	int S;
	int tot;
	int fi[MAXN];
	int dep[MAXN];
	int fa[MAXN][20];
	int ne[MAXN << 1];
	int to[MAXN << 1];

	inline void Link(int u, int v) {
		tot++;
		to[tot] = v;
		ne[tot] = fi[u];
		fi[u] = tot;
	}

	inline void dfs(int x, int la) {
		fa[x][0] = la, dep[x] = dep[la] + 1;
		for(int i = 1; i < 20; i++) fa[x][i] = fa[fa[x][i - 1]][i - 1];
		for(int i = fi[x]; i; i = ne[i]) {
			int u = to[i];
			if(u == la) continue;
			dfs(u, x);
		}
	}

	inline int LCA(int x, int y) {
		if(dep[x] < dep[y]) swap(x, y);
		int k = dep[x] - dep[y];
		for(int i = 0; i < 20; i++)
		 	if(k & (1 << i)) x = fa[x][i];
		if(x == y) return x;
		for(int i = 19; ~i; --i)
			if(fa[x][i] != fa[y][i])
				x = fa[x][i], y = fa[y][i];
		return fa[x][0];
	}

	inline int dis(int u, int v) { return dep[u] + dep[v] - dep[LCA(u, v)] * 2; }
	inline int dis_S(int x) { return dep[x] - 1; }

	inline void AddEdge(int ty = 0) {
		for(int i = 1; i < n; i++) {
			int u = ri, v = ri;
			Link(u, v), Link(v, u);
			if(ty) E.push_back(make_pair(u, v));
		} dfs(S, S);
	}
