#include <cstdio>
#include <algorithm>
#include <vector>
using namespace std;

const int MAXN = 200005;
const int LOG = 18;

int N, X, Y;
vector <int> red[MAXN], blue[MAXN];
int anc[MAXN][LOG];
int disc[MAXN], fin[MAXN];
int depth[MAXN];
int timer = 1;
int sol;

void load() {
	scanf("%d%d%d", &N, &X, &Y);
	for (int i = 1; i < N; i++) {
		int a, b;
		scanf("%d%d", &a, &b);
		red[a].push_back(b);
		red[b].push_back(a);
	}
	for (int i = 1; i < N; i++) {
		int c, d;
		scanf("%d%d", &c, &d);
		blue[c].push_back(d);
		blue[d].push_back(c);
	}
}

void dfs(int x, int p) {
	disc[x] = timer++;
	depth[x] = depth[p] + 1;
	anc[x][0] = p;
	for (auto it : blue[x])
		if (it != p)
			dfs(it, x);
	fin[x] = timer;
}

int jmp(int x, int k) {
	for (int i = 0; i < LOG; i++)
		if (k >> i & 1)
			x = anc[x][i];
	return x;
}

int lca(int x, int y) {
	if (depth[x] < depth[y])
		swap(x, y);
	x = jmp(x, depth[x] - depth[y]);
	if (x == y)
		return x;
	for (int i = LOG - 1; i >= 0; i--)
		if (anc[x][i] != anc[y][i]) {
			x = anc[x][i];
			y = anc[y][i];
		}
	return anc[x][0];
}

int dist(int x, int y) {
	return depth[x] + depth[y] - 2 * depth[lca(x, y)];
}

int get_nxt(int x, int y) {
	if (x == y)
		return x;
	if (disc[y] < disc[x] || disc[y] >= fin[x])
		return anc[x][0];
	return jmp(y, depth[y] - depth[x] - 1);
}

void go(int x, int p, int pos, int steps) {
	sol = max(sol, steps + dist(pos, x));
	for (auto it : red[x]) 
		if (it != p) {
			int tmp = get_nxt(pos, it);
			if (tmp == it)
				continue;
			if (dist(x, it) >= 3)
				sol = MAXN;
			go(it, x, tmp, steps + 1);
		}
}

int solve() {
	dfs(1, 0);	

	for (int j = 1; j < LOG; j++)
		for (int i = 1; i <= N; i++)
			anc[i][j] = anc[anc[i][j - 1]][j - 1];

	go(X, 0, Y, 0);

	return sol < MAXN ? 2 * sol : -1;
}

int main() {
	load();
	printf("%d\n", solve());
	return 0;
}

