            if (!atMost2(i, red[i][j]))
                safe[i] = safe[red[i][j]] = 1;
    if (safe[X]) {puts("-1"); return 0;}
    memset(dp, -1, sizeof(dp));
    queue<int> q;
    q.push(X); dp[X] = 0;
    int ans = 0;
    while (!q.empty()) {
        int u = q.front(); q.pop();
        if (dp[u] >= dep[u]) continue;
        if (safe[u]) {puts("-1"); return 0;}
        ans = max(ans, dep[u] * 2);
        for (int i=0; i<red[u].size(); i++) {
            int v = red[u][i];
            if (dp[u] + 1 < dep[v] && atMost2(u, v)) {
                dp[v] = dp[u] + 1;
                q.push(v);
            }
        }
    }
    printf("%d\n", ans);
    return 0;
}

#include <cstdio>

const int N=200005;

int fst[N],dep[N],d[N],a[N],b[N],fa[N],tot=0,ans=0;
bool u[N];
struct Edge{
	int to,nxt;
}e[N*2];

void addedge(int x,int y){
	e[++tot].to=y;e[tot].nxt=fst[x];fst[x]=tot;
}

void dfs(int x){
	for (int i=fst[x];i;i=e[i].nxt){
		int y=e[i].to;
		if (y!=fa[x]){
			fa[y]=x;
			dep[y]=dep[x]+1;
			dfs(y);
		}
	}
}

void dfs2(int x,int v){
	if (ans==-1 || d[x]>=dep[x]) return;
	if (u[x]) ans=-1;
		else if (dep[x]*2>ans) ans=dep[x]*2;
	for (int i=fst[x];i;i=e[i].nxt){
		int y=e[i].to;
		if (y!=v){
			d[y]=d[x]+1;
			dfs2(y,x);
		}
	}
}

int main(){
	int n,X,Y;
	scanf("%d%d%d",&n,&X,&Y);
	for (int i=1;i<n;i++) scanf("%d%d\n",&a[i],&b[i]);
	for (int i=1;i<n;i++){
		int x,y;
		scanf("%d%d",&x,&y);
		addedge(x,y);
		addedge(y,x);
	}
	dfs(Y);
	for (int i=1;i<=n;i++) fst[i]=0;
	tot=0;
	for (int i=1;i<n;i++){
		int x=a[i],y=b[i],fx=fa[x],fy=fa[y];
		if (fx==y||fy==x||fa[fx]==y||fa[fy]==x||fx==fy) addedge(x,y),addedge(y,x);
			else u[x]=u[y]=1;
	}
	dfs2(X,0);
	printf("%d\n",ans);
}

