int lca(int u,int v){
	if(dep[u]<dep[v])u^=v,v^=u,u^=v;
	for(int i=19;~i;--i)
		if(dep[fa[i][u]]>dep[v])
			u=fa[i][u];
	if(dep[u]>dep[v])
