const int MAXN=200005;

int n,X,Y,ncnt;
int dfn[MAXN],efn[MAXN],fa[MAXN],dep[MAXN];
struct node{
    int v,nex;
}E[MAXN*2],G[MAXN*2];
int Ehead[MAXN],Ghead[MAXN],dcnt[2];
bool tag[MAXN];
pair<int,int> a[MAXN];

void addedge(node edge[],int head[],int &cnt,int u,int v){
    node &p=edge[++cnt];
    p.v=v;
    p.nex=head[u];
    head[u]=cnt;
}

void dfs(int u,int fa=0){
    ::fa[u]=fa;
    dep[u]=dep[fa]+1;
    dfn[u]=++ncnt;
    for(int i=Ghead[u];i;i=G[i].nex){
        int v=G[i].v;
        if(v==fa) continue;
        dfs(v,u);
    }
    efn[u]=++ncnt;
}

bool check(int u,int v){
    if(dfn[u]>dfn[v]) swap(u,v);
    if(dfn[u]<dfn[v]&&efn[v]<efn[u])
        return dep[v]-dep[u]>=3;
    return fa[u]!=fa[v];
}

int vis[MAXN],dis[MAXN],deq[MAXN],l,r;
void bfs(){
    for(int i=1;i<=n;i++) vis[i]=dis[i]=0;
    l=r=1;
    deq[r++]=X;
    while(l<r){
        int u=deq[l++];
        vis[u]=1;
        for(int i=Ehead[u];i;i=E[i].nex){
            int v=E[i].v;
            if(vis[v]) continue;
            dis[v]=dis[u]+1;
            if(dis[v]<dep[v])
                deq[r++]=v;
        }
    }
}

