        for(int i=1;i<=n;i++) Ehead[i]=Ghead[i]=0;
        dcnt[0]=dcnt[1]=ncnt=0;
        for(int i=1;i<=n;i++) tag[i]=0;
        for(int i=1;i<n;i++) scanf("%d%d",&a[i].first,&a[i].second);
        for(int i=1;i<n;i++){
            int u,v;
            scanf("%d%d",&u,&v);
            addedge(G,Ghead,dcnt[1],u,v);
            addedge(G,Ghead,dcnt[1],v,u);
        }
        dfs(Y);
        for(int i=1;i<n;i++)
            if(check(a[i].first,a[i].second)) tag[a[i].first]=tag[a[i].second]=1;
            else addedge(E,Ehead,dcnt[0],a[i].first,a[i].second),addedge(E,Ehead,dcnt[0],a[i].second,a[i].first);
        bfs();
        int ans=0;
        bool flag=1;
        for(int i=1;i<=n&&flag;i++){
            if(!vis[i]) continue;
            if(tag[i]) flag=0;
            else ans=max(ans,2*dep[i]);
        }
        printf("%d\n",flag?ans:-1);
    }

    fclose(stdin);
    fclose(stdout);
}

