#include<bits/stdc++.h>
using namespace std;

int N,X,Y;
int par[20][200010],dep[200010];
vector<int> R[200010],B[200010];
int INF = 1001001001;
int ans = 0;
bool in[200010];

int LCA(int x,int y)
{
    if(dep[x] > dep[y])swap(x,y);
    int dif = dep[y] - dep[x];
    for(int i = 19; i >= 0; i--)if(dif & (1 << i))y = par[i][y];
    if(x == y)return x;
    for(int i = 19; i >= 0; i--)
    {
        if(par[i][x] != par[i][y])
        {
            x = par[i][x];
            y = par[i][y];
        }
    }
    return par[0][x];
}

int dis(int x,int y)
{
    return dep[x] + dep[y] - 2 * dep[LCA(x,y)];
}

void DFS(int v,int p,int d)
{
    par[0][v] = p;
    dep[v] = d;
    for(int i = 0; i < B[v].size(); i++)
    {
        int w = B[v][i];
        if(w == p)continue;
        DFS(w,v,d + 1);
    }
    return;
}

void DFS2(int v,int p)
{
    in[v] = true;
    for(int i = 0; i < R[v].size(); i++)
    {
        int w = R[v][i];
        if(w == p || dis(v,w) > 2)continue;
        DFS2(w,v);
    }
    return;
}

void DFS3(int v,int p,int d)
{
    if(d >= dis(Y,v) || !in[v])return;
    ans = max(ans,dis(Y,v) * 2);
    for(int i = 0; i < R[v].size(); i++)
    {
        int w = R[v][i];
        if(w == p)continue;
        if(!in[w])ans = INF;
        DFS3(w,v,d + 1);
    }
    return;
}

int main()
{
    scanf("%d%d%d",&N,&X,&Y);
    for(int i = 0; i < N - 1; i++)
    {
        int a,b;
        scanf("%d%d",&a,&b);
        R[a].push_back(b);
        R[b].push_back(a);
    }
    for(int i = 0; i < N - 1; i++)
    {
        int a,b;
        scanf("%d%d",&a,&b);
        B[a].push_back(b);
        B[b].push_back(a);
    }

    DFS(1,0,0);

    for(int i = 1; i < 20; i++)
    {
        for(int j = 1; j <= N; j++)
        {
            if(par[i - 1][j] == 0)par[i][j] = 0;
            else par[i][j] = par[i - 1][par[i - 1][j]];
        }
    }

    DFS2(X,0);

    DFS3(X,0,0);

    if(ans == INF)printf("-1\n");
    else printf("%d\n",ans);
    return 0;
}

