	{
		if (check(a[i][0],a[i][1])) win[a[i][0]]=win[a[i][1]]=1;
		else addedge(a[i][0],a[i][1],0);
	}
	bfs();
	for (int i=1;i<=n;i++) if (win[i]&&vis[i]) {puts("-1");return 0;}
	int ans=0;
	for (int i=1;i<=n;i++) if (vis[i]) ans=max(ans,dep[i]*2);
