using namespace std;
struct lca_finder{
    int size, lg;
    vector<vector<int> > lca;
    vector<int> depths;
    lca_finder(vector<int> & parents, vector<int> & dep){
        // root should have -1 as parent
        size = parents.size();
        depths = dep;
        lg = log2(size) + 2;
        lca.assign(lg, vector<int>(size));
        for(int e = 0; e < size; e++)
            lca[0][e] = parents[e];
        build();
    }
    void build(){
        for(int e = 1; e < lg; e++)
            for(int f = 0; f < size; f++)
                if(lca[e-1][f] != -1)
                    lca[e][f] = lca[e-1][lca[e-1][f]];
    }
    int get_lca(int u, int v){
        if(depths[u] < depths[v]) swap(u, v);
        for(int e = lg - 1; e >= 0; e--)
            if((depths[u] - depths[v]) & (1 << e))
                u = lca[e][u];
        if(u == v) return u;
        for(int e = lg - 1; e >= 0; e--)
            if(lca[e][u] != lca[e][v]){
                u = lca[e][u];
                v = lca[e][v];
            }
        return lca[0][u];
    }
};
