const int N=200010;
int n,X,Y,f[N][20],dep[N],vis[N],ans;

struct graph{
	struct edge{int s,t,n;}e[4*N];
	int h[N];

	void init()
	{
		for (int i=1,u,v,tot=0; i<n; i++)
			{
				scanf("%d %d",&u,&v);
				e[++tot]=(edge){u,v,h[u]},h[u]=tot;
				e[++tot]=(edge){v,u,h[v]},h[v]=tot;
			}
	}	

	int dis(int x,int y)
	{
		int s=0;
		if (dep[x]>dep[y])  swap(x,y);
		for (int i=19; i>=0; i--)
			if (dep[f[y][i]]>=dep[x])
				y=f[y][i],s+=1<<i;
		if (x==y)  return s;
		for (int i=19; i>=0; i--)
			if (f[x][i]!=f[y][i])
				x=f[x][i],y=f[y][i],s+=2<<i;
		return s+2;
	}

	bool dfs(int x,int fa)
	{
		int _=(x==X);
		f[x][0]=fa,dep[x]=dep[fa]+1,vis[x]=vis[0];
		for (int i=1; i<20; i++)  f[x][i]=f[f[x][i-1]][i-1];
		for (int i=h[x],y; y=e[i].t,i; i=e[i].n)
			if ((y!=fa)&&(dfs(y,x)))  _=1;
		return _;
	}

	void mark()
	{
		int _;
		dep[0]=-1;
		for (int i=h[Y],y; y=e[i].t,i; i=e[i].n)
			if (vis[0]++,dfs(y,Y))  _=vis[0];
		vis[0]=_;
	}

	void dfs2(int x,int tim)
	{
		ans=max(ans,dep[x]),vis[x]=vis[0]+1;
		for (int i=h[x],y; y=e[i].t,i; i=e[i].n)
			if ((vis[y]==vis[0])&&(dep[y]>tim))
				dfs2(y,tim+1);
	}
}A,B;

void work()
{
	scanf("%d %d %d",&n,&X,&Y);
	A.init(),B.init(),B.mark(),A.dfs2(X,1);
	for (int i=2; i<2*n; i+=2)
		if ((vis[A.e[i].s]>vis[0])||(vis[A.e[i].t]>vis[0]))
			if (B.dis(A.e[i].s,A.e[i].t)>2)
				puts("-1"),exit(0);
	printf("%d",2*ans);
}

