const int N = (int)2e5 + 228;
const int LOG = 20;

using namespace std;

int n, x, y, tin[N], tout[N], timer, up[LOG][N], depth[N], ans;
vector < int > g[N], t[N];

void dfs1(int v, int pr)
{
	tin[v] = ++timer;
	for (auto to : t[v])
	{
		if (to == pr)
			continue;
		up[0][to] = v;
		depth[to] = depth[v] + 1;
		dfs1(to, v);	
	}
	tout[v] = timer;
}

bool upper(int v, int u)
{
	return tin[v] <= tin[u] && tout[v] >= tout[u];
}

int lift(int v, int u)
{
	for (int i = LOG - 1; i >= 0; i--)
	{
		if (up[i][v] && !upper(up[i][v], u))
			v = up[i][v];
	}
	return v;
}

int lca(int v, int u)
{
	if (upper(v, u))
		return v;
	if (upper(u, v))
		return u;
	v = lift(v, u);
	return up[0][v];
}

int dist(int v, int u)
{
	int pr = lca(v, u);
	return depth[v] + depth[u] - 2 * depth[pr];
}

void dfs(int v, int pr, int u, int cnt)
{
	ans = max(ans, cnt + 2 * dist(v, u));
	if (u == v)
		return;	
	for (auto to : g[v])
	{
		if (to == pr || to == u)
			continue;	
		int newU = up[0][u];
		if (upper(u, to))
			newU = lift(to, u);
		dfs(to, v, newU, cnt + 2);
		if (dist(v, to) > 2)
		{
			cout << -1 << endl;
			exit(0);
		}
	}
}

