    return false;
  }
  x--;
  y--;
  for (int i = 0; i < n; i++) {
    gr[i].clear();
    gb[i].clear();
  }
  for (int i = 0; i < n - 1; i++) {
    int v, u;
    scanf("%d%d", &v, &u);
    v--;
    u--;
    gr[v].push_back(u);
    gr[u].push_back(v);
  }
  for (int i = 0; i < n - 1; i++) {
