    inline void dfs2(int u,int anc) {
        top[u]=anc;
        if (hson[u]) dfs2(hson[u],anc);
        for (re int i=head[u];i;i=e[i].nxt)
            if (e[i].v!=fa[u]&&e[i].v!=hson[u]) dfs2(e[i].v,e[i].v);
