const int maxn = 2e5 + 100;

const int lgn = 19;

int n, r1, r2, ans;
vi g1[maxn], g2[maxn];
int par1[maxn][lgn], par2[maxn][lgn];
int dep1[maxn], dep2[maxn];

void dfs1(int u = r1, int p = 0) {
	par1[u][0] = p;
	forn(i, 1, lgn - 1) {
		par1[u][i] = par1[par1[u][i - 1]][i - 1];
	}
	dep1[u] = dep1[p] + 1;
	for (int v: g1[u]) {
		if(v != p) {
			dfs1(v, u);
		}
	}
	return;
}

void dfs2(int u = r2, int p = 0) {
	par2[u][0] = p;
	forn(i, 1, lgn - 1) {
		par2[u][i] = par2[par2[u][i - 1]][i - 1];
	}
	dep2[u] = dep2[p] + 1;
	for (int v: g2[u]) {
		if(v != p) {
			dfs2(v, u);
		}
	}
	return;
}

int lca(int u, int v) {
	if(dep2[u] < dep2[v]) {
		swap(u, v);
	}
	int dlt = dep2[u] - dep2[v];
	rep(i, lgn) {
		if((dlt >> i) & 1) {
			u = par2[u][i];
		}
	}
	if(u == v) {
		return u;
	}
	per(i, lgn) {
		if(par2[u][i] != par2[v][i]) {
			u = par2[u][i], v = par2[v][i];
		}
	}
	return par2[u][0];
}

void dfs3(int u = r1, int p = 0) {
	debug("dfs3(%d, %d)\n", u, p);
	chkmax(ans, max(dep1[u], dep2[u]) - 1 << 1);
	if(dep1[u] >= dep2[u]) {
		return;
	}
	if(p) {
		int f = lca(u, p);
		if(dep2[u] + dep2[p] - dep2[f] * 2 > 2) {
			ans = -1;
			return;
		}
	}
	for (int v: g1[u]) {
		if(v != p) {
			dfs3(v, u);
		}
	}
	return;
}

