	}

	int solve(int u,int f,int dis)
	{
//		printf("u=%d\n",u);
		int Ans=dep[u];
		for (int p=FIR[u];p;p=NEXT[p])
		{
			int v=TO[p];
			if (v==f) continue;
			if (dis+1>=dep[v]) continue;
			bool flag=fa[v]==u||fa[u]==v||fa[fa[v]]==u||fa[fa[u]]==v||fa[u]==fa[v];
			if (!flag) return 1e8;
//			printf("solving %d : %d--->%d\n",u,u,v);
			Ans=max(Ans,solve(v,u,dis+1));
		}
		return Ans;
	}
}red,blue;

