const int N = 2e5 + 5, M = N * 2 , inf = 1e9 + 7;
using namespace std ;

bool vis[N], path[N] ;
int n, rt, S, e, ter[M], nxt[M], lnk[N], dep[N], fa[N], ans ;
struct edge {
	int x, y ;
} ed[N] ;

void add(int x, int y) {
	ter[++ e] = y, nxt[e] = lnk[x], lnk[x] = e ;
}

void dfs(int p, int las) {
	for (int i = lnk[p] ; i; i = nxt[i]) if (ter[i] != las) {
		dep[ter[i]] = dep[p] + 1 ;
		fa[ter[i]] = p ;
		dfs(ter[i], p) ;
	}
}

void dfs2(int p, int las, int len) {
	if (ans == inf) return ;
	if (dep[p] < len) {
		ans = max(ans, (len - 1) * 2 + 1) ;
		return ;
	}
	if (dep[p] == len) {
		ans = max(ans, len * 2) ;
		return ;
	}
	if (vis[p]) {
		ans = inf ;
		return ;
	}
	bool flg = false ;
	for (int i = lnk[p] ; i; i = nxt[i]) if (ter[i] != las) flg = true, dfs2(ter[i], p, len + 1) ;
	if (!flg) {
		ans = max(ans, dep[p] * 2) ;
		return ;
	}
}

bool chk(int x, int y) {
	if (dep[x] < dep[y]) swap(x, y) ;
	if (fa[x] == y) return false ;
	if (fa[fa[x]] == y) return false ;
	if (fa[x] == fa[y]) return false ;
	return true ;
}

int main() {
	scanf("%d%d%d", &n, &S, &rt) ;
	rep(i, 1, n - 1) scanf("%d%d", &ed[i].x, &ed[i].y) ;
	int x, y ;
	rep(i, 1, n - 1) {
		scanf("%d%d", &x, &y) ;
		add(x, y), add(y, x) ;
	}
	dfs(rt, 0) ;
	rep(i, 1, n - 1) if (chk(ed[i].x, ed[i].y)) vis[ed[i].x] = vis[ed[i].y] = true ;
	rep(i, 1, n) lnk[i] = 0 ;
	rep(i, 1, e) nxt[i] = 0 ; e = 0 ;
	rep(i, 1, n - 1) add(ed[i].x, ed[i].y), add(ed[i].y, ed[i].x) ;
	dfs2(S, 0, 0) ;
	if (ans == (int) (1e9 + 7)) {
		printf("-1\n") ;
	} else printf("%d\n", ans) ;
	return 0 ; 
}

