using LL = long long;
constexpr LL LINF=334ll<<53;
constexpr int INF=15<<26;
constexpr LL  MOD=1E9+7;
struct Edge{
    int from,to;
    Edge(int from,int to)
        : from(from),to(to){};
    Edge(){Edge(0,0);}
};
typedef vector<vector<Edge>> Graph;

struct HeavyLightDecomposition{
    vector<int> index, htop, hchild, par, dep, idtov, topo,sub,heads;
    Graph T;
    HeavyLightDecomposition(int n) :index(n,-1),htop(n),hchild(n,-1),par(n),dep(n),idtov(n),sub(n),T(n){}

    void calc(int root){
        queue<int> Q({root});
        par[root]=-1;
        while(!Q.empty()){
            int curr=Q.front(); Q.pop(); topo.push_back(curr);
            for(auto &x: T[curr]){
                if(x.to!=par[curr]){
                    par[x.to]=x.from;
                    Q.push(x.to);
                    dep[x.to]=dep[x.from]+1;
                }
            }
        }
        heads.push_back(root);
        for(int i=par.size()-1,msub=0; i>=0; --i){
            ++sub[topo[i]];
            if(i>0)sub[par[topo[i]]]+=sub[topo[i]];
            if(i<par.size()-1)if(par[topo[i+1]]!=par[topo[i]])msub=0;
            if(sub[topo[i]]>msub) hchild[par[topo[i]]]=topo[i];
            for(auto &x : T[topo[i]])if(x.to!=par[topo[i]]&&x.to!=hchild[topo[i]]) heads.push_back(x.to);
        }
        int id=0;
        for(int x : heads) {
            for(int i=x; i!=-1; i=hchild[i]){
                index[i]=id++;
                idtov[index[i]]=i;
                htop[i]=x;
            }
        }
    }
    inline bool same(int a,int b){return htop[a]==htop[b];}

    int lca(int a, int b){
        if(index[a]>index[b])swap(a,b);
        if(same(a,b)) return a;
        return lca(a,par[htop[b]]);
    }
    int dist(int a, int b){
        return dep[a]+dep[b]-2*dep[lca(a,b)];
    }
};

struct Problem{
    int size,gma,gim;
    HeavyLightDecomposition sigma,sugim;
    Problem(int n):size(n),sigma(n),sugim(n){};
    void input(HeavyLightDecomposition &hl){
        for(int i=0; i<size-1; ++i){
            int a,b;
            cin >> a >> b;
            --a;--b;
            hl.T[a].push_back(Edge(a,b));
            hl.T[b].push_back(Edge(b,a));
        }
    }
    bool is_sigma_nearer(int i){
        return sigma.dist(i,gma)<sugim.dist(i,gim);
    }
    int dfs(int par, int cur){
        if(!is_sigma_nearer(cur)) return -INF;
        if(par!=-1 and sugim.dist(par,cur)>=3) return INF;
        if(sigma.T[cur].size()==1 and sigma.T[cur][0].to==par){
            return sugim.dist(cur,gim);
        }
        int ret=sugim.dist(cur,gim);
        for(auto e: sigma.T[cur]){
            if(e.to!=par){
                ret=max(ret,dfs(cur,e.to));
            }
        }
        return ret;
    }

    void solve(){
        cin >> gma >> gim;
        --gma;--gim;
        input(sigma);
        input(sugim);
        sigma.calc(gma);
        sugim.calc(gim);
        int ans=dfs(-1,gma);
        if(ans==INF)cout << -1 <<"\n";
        else cout << ans*2<<"\n";
    }

};

