int n, x, y;
vector<int> G[200005], g[200005], g2[200005];
bool valid[200005], win[200005];
int dist[200005], dist2[200005];

int Prev[200005][18];
int depth[200005];

int getLCA(int u, int v){
	int x = u, y = v;
	if(depth[y] > depth[x]) swap(x, y);

	for(int i = 17; i >= 0; i--){
		if(depth[x] - (1<<i) >= depth[y]) x = Prev[x][i];
	}
	if(x == y) return x;
	for(int i = 17; i >= 0; i--){
		if(Prev[x][i] != Prev[y][i]){
			x = Prev[x][i];
			y = Prev[y][i];
		}
	}
	x = Prev[x][0];
	return x;
}

void predfs(int v, int p, int d)
{
	depth[v] = d;
	Prev[v][0] = p;
	for(int i = 0; i < G[v].size(); i++){
		if(G[v][i] == p) continue;
		predfs(G[v][i], v, d+1);
	}
}

void dfs(int v, int p, int d, int dist[], vector<int> G[])
{
	dist[v] = d;
	for(int i = 0; i < G[v].size(); i++){
		if(G[v][i] == p) continue;
		dfs(G[v][i], v, d+1, dist, G);
	}
}

int dfs2(int v, int p)
{
	if(win[v]) return -1;

	int ret = dist[v];
	for(int i = 0; i < g2[v].size(); i++){
		if(g2[v][i] == p) continue;
		if(!valid[g2[v][i]]) continue;
		int res = dfs2(g2[v][i], v);
		if(res == -1) return -1;
		ret = max(ret, res);
	}
	return ret;
}

int main(void)
{
	cin >> n >> x >> y;
	int u, v;
	for(int i = 1; i <= n-1; i++){
		cin >> u >> v;
		g[u].push_back(v);
		g[v].push_back(u);
	}
	for(int i = 1; i <= n-1; i++){
		cin >> u >> v;
		G[u].push_back(v);
		G[v].push_back(u);
	}

	predfs(1, 0, 0);
	for(int i = 1; i < 18; i++){
		for(int j = 1; j <= n; j++){
			Prev[j][i] = Prev[Prev[j][i-1]][i-1];
		}
	}

	for(int i = 1; i <= n; i++){
		for(int j = 0; j < g[i].size(); j++){
			int dist = depth[i] + depth[g[i][j]] - 2*depth[getLCA(i, g[i][j])];
			if(dist >= 3) win[i] = win[g[i][j]] = true;
			else g2[i].push_back(g[i][j]);
		}
	}

	dfs(x, -1, 0, dist2, g2);
	dfs(y, -1, 0, dist, G);

	for(int i = 1; i <= n; i++){
		if(dist[i] > dist2[i]) valid[i] = true;
	}

	int ret = dfs2(x, -1);
	if(ret != -1) ret *= 2;
	cout << ret << endl;

	return 0;
}

