        for( int j : axe[i] ) {
            bool ok = true;
            int x = i, y = j;

            if( lev[x] > lev[y] )
                swap( x, y );

            if( fth[x] == fth[y] ) ok = false;
            if( fth[fth[y]] == x ) ok = false;
            if( fth[y] == x )      ok = false;

            if( ok == true )
                oki[x] = oki[y] = true;
        }
