            dfs(it, x, v);
        }
    }
}
void build(int n){
    for(int i = 1 ; i < 20 ; i++){
        for(int j = 1 ; j <= n ; j++){
            p[ i ][ j ] = p[ i - 1 ][ p[ i - 1 ][ j ] ];
        }
    }
}
int lca(int x, int y){
    if (dis[ x ] > dis[ y ])swap(x, y);
    int d = dis[ y ] - dis[ x ];
    for(int i = 0 ; i < 20 ; i++ ){
        if( d & (1 << i) ){
            y = p[ i ][ y ];
        }
    }
    if (x == y)
        return x;
    for(int i = 19 ; i >= 0 ; i-- ){
        if( p[ i ][ x ] != p[ i ][ y ] ){
            x = p[ i ][ x ];
            y = p[ i ][ y ];
        }
    } 
    return p[ 0 ][ x ];
}
int getdis(int x, int y){
    return dis[ x ] + dis[ y ] - dis[ lca(x, y) ];
}
void dfs2(int x,int f,vector<int> *v,int sum=0){
    if(dis[x]<=sum)return;
    for(auto it:v[x]){
        if( it != f){
            if(getdis(x,it)>=3&&dis[x] > sum){
              //  printf("%d %d %d %d %d\n",x,it,dis[x],sum,getdis(x,it));
                printf("-1\n");
                exit(0);
            }
            dfs2(it, x, v, sum+1);
        }
    }
}
int main(){
    int n, x, y;
    scanf("%d %d %d", &n, &x, &y);
    for(int i = 0 ; i < 2 ; i++ ){
        for(int j = 1 ; j < n ; j++ ){
            int x, y;
            scanf("%d %d", &x, &y);
            v[i][x].pb(y);
            v[i][y].pb(x);
        }
    }
    dis[0]=-1;
    dfs(y, 0, v[1]);
    build(n);
    dfs2(x, 0, v[0]);
}
