const int N=200010;
int n,X,Y,Dep[N],vis[N],ans;

struct graph{
	struct edge{int s,t,n;}e[4*N];
	int h[N],f[N][20],dep[N];

	void dfs1(int x,int fa)
	{
		f[x][0]=fa,dep[x]=dep[fa]+1;
		for (int i=1; i<20; i++)  f[x][i]=f[f[x][i-1]][i-1];
		for (int i=h[x],y; y=e[i].t,i; i=e[i].n)  if (y!=fa)  dfs1(y,x);
	}

	void init()
	{
		for (int i=1,u,v,tot=0; i<n; i++)
			{
				scanf("%d %d",&u,&v);
				e[++tot]=(edge){u,v,h[u]},h[u]=tot;
				e[++tot]=(edge){v,u,h[v]},h[v]=tot;
			}
		dfs1(1,0);
	}	

	int dis(int x,int y)
	{
		int s=0;
		if (dep[x]>dep[y])  swap(x,y);
		for (int i=19; i>=0; i--)
			if (dep[f[y][i]]>=dep[x])
				y=f[y][i],s+=1<<i;
		if (x==y)  return s;
		for (int i=19; i>=0; i--)
			if (f[x][i]!=f[y][i])
				x=f[x][i],y=f[y][i],s+=2<<i;
		return s+2;
	}

	bool dfs2(int x,int f)
	{
		int _=(x==X);
		Dep[x]=Dep[f]+1,vis[x]=vis[0];
		for (int i=h[x],y; y=e[i].t,i; i=e[i].n)
			if ((y!=f)&&(dfs2(y,x)))  _=1;
		return _;
	}

	void mark()
	{
		for (int i=h[Y],y; y=e[i].t,i; i=e[i].n)
			if (vis[0]++,dfs2(y,Y))  return;
	}

	void dfs3(int x,int f)
	{
		ans=max(ans,Dep[x]);
		for (int i=h[x],y; y=e[i].t,i; i=e[i].n)
			if ((y!=f)&&(vis[y]))  dfs3(y,x);
	}
}A,B;

void work()
{
	scanf("%d %d %d",&n,&X,&Y);
	A.init(),B.init(),B.mark();
	for (int i=1; i<=n; i++)  vis[i]=(vis[i]==vis[0]);
	for (int i=2; i<2*n; i+=2)
		if (((vis[A.e[i].s])||(vis[A.e[i].t]))&&(B.dis(A.e[i].s,A.e[i].t)>2))
			puts("-1"),exit(0);
	A.dfs3(X,0);
	printf("%d",2*ans);
}

int main()
{
	work();
	return 0;
}

