const int maxn = 2e5 + 5;
const int mlog = 18;
int n,a,b;
vector<int> way1[maxn], way2[maxn];
int depA[maxn], depB[maxn];
int par[maxn][20];
int good[maxn];
int loop, ans;
void build(int u, int last) {
    par[u][0] = last;
    for(int i=1;i<=mlog;i++) par[u][i] = par[par[u][i-1]][i-1];
    for(auto v : way2[u]) {
        if(v==last) continue;
        depB[v] = depB[u] + 1;
        build(v, u);
    }
}
void dfs(int u, int last) {
    if(depB[u]<=depA[u]) return ;
    if(good[u]) loop = 1;
    else ans = max(ans, depB[u]*2);
    for(auto v : way1[u]) {
        if(v==last) continue;
        depA[v] = depA[u] + 1;
        dfs(v, u);
    }
}
int lca(int u, int v) {
    int res = 0;
    if(depB[u]<depB[v]) swap(u,v);
    for(int i=mlog;i>=0;i--) {
        if(par[u][i] && depB[par[u][i]]>=depB[v]) {
            u = par[u][i];
            res += (1<<i);
        }
    }
    if(u==v) return res;
    for(int i=mlog;i>=0;i--) {
        if(par[u][i]!=par[v][i]) {
            u = par[u][i]; v = par[v][i];
            res += (1<<i)*2;
        }
    }
    return res+2;
}
int main() {
    scanf("%d%d%d",&n,&a,&b);
    for(int i=1;i<=n-1;i++) {
        int u,v; scanf("%d%d",&u,&v);
