        way1[v].push_back(u);
    }
    for(int i=1;i<=n-1;i++) {
        int u,v; scanf("%d%d",&u,&v);
        way2[u].push_back(v);
        way2[v].push_back(u);
    }
    return 0;
    build(b,0);
    return 0;
    for(int u=1;u<=n;u++) {
        for(auto v : way1[u]) {
            if(lca(u,v)>=3) {
                good[u] = good[v] = 1;
            }
        }
    }
//    for(int u=1;u<=n;u++) printf("%d : %d\n",u,good[u]);
    dfs(a,0);
    if(loop) printf("-1");
    else printf("%d",ans);
}

