#include <bits/stdc++.h>
using namespace std;
const int N = 200010;
vector<int> a[N], b[N];
int da[N], db[N];
int id[N], q[N << 1], dph[N << 1], dfn = 0;
int dp[N << 1][20], lg2[N];
int n, x, y;
void dfsb(int x, int fx) {
    id[x] = ++dfn;
    q[dfn] = x;
    db[x] = db[fx] + 1;
    dph[dfn] = db[x];
    for (int y : b[x]) {
        if (y != fx) dfsb(y, x);
        q[++dfn] = x;
        dph[dfn] = db[x];
    }
}
void RMQ_init(int n) {
    for (int i = 2; i <= n; i++) lg2[i] = lg2[i / 2] + 1;
    for (int i = 1; i <= n; i++) dp[i][0] = i;
    for (int j = 1; (1 << j) <= n; j++) {
        for (int i = 1; i + (1 << j) - 1 <= n; i++) {
            int a = dp[i][j - 1], b = dp[i + (1 << (j - 1))][j - 1];
            dp[i][j] = dph[a] <= dph[b] ? a : b;
        }
    }
}
int RMQ_min(int L, int R) {
    if (L > R) swap(L, R);
    int k = lg2[R - L + 1];
    int a = dp[L][k], b = dp[R - (1 << k) + 1][k];
    return dph[a] <= dph[b] ? a : b;
}
int LCA(int u, int v) {
    int x = id[u], y = id[v];
    return q[RMQ_min(x, y)];
}
int ans = 0;
void dfsa(int x, int fx) {
    da[x] = da[fx] + 1;
    if (da[x] >= db[x]) return;
    if (~ans) ans = max((db[x] - 1) << 1, ans);
    for (int y : a[x]) {
        if (y == fx) continue;
        if (db[x] + db[y] - 2 * db[LCA(x, y)] > 2) ans = -1;
        dfsa(y, x);
    }
}
int main() {
    scanf("%d%d%d", &n, &x, &y);
    for (int i = 1; i < n; i++) {
        int u, v;
        scanf("%d%d", &u, &v);
        a[u].push_back(v);
        a[v].push_back(u);
    }
    for (int i = 1; i < n; i++) {
        int u, v;
        scanf("%d%d", &u, &v);
        b[u].push_back(v);
        b[v].push_back(u);
    }
    dfsb(y, 0);
    RMQ_init(dfn);
    dfsa(x, 0);
    printf("%d\n", ans);
    return 0;
}

