const int MN = 200010;

int N, X, Y;
vector<int> adj1[MN], adj2[MN];
int par[20][MN], dep[MN], tin[MN], tout[MN], timer;

void dfs0(int u, int p) {
    tin[u] = timer++;

    par[0][u] = p;
    for(int i = 1; i < 20; i++) {
        int t = par[i - 1][u];
        if(t == -1) break;
        par[i][u] = par[i - 1][t];
    }

    for(int i = 0; i < adj2[u].size(); i++) {
        int v = adj2[u][i];
        if(v == p) continue;
        dep[v] = dep[u] + 1;
        dfs0(v, u);
    }

    tout[u] = timer;
}

int lca(int a, int b) {
    if(dep[a] < dep[b]) swap(a, b);
    int diff = dep[a] - dep[b];
    for(int i = 0; i < 20; i++) {
        if(diff & (1 << i)) a = par[i][a];
    }
    if(a == b) return a;
    for(int i = 20; i--;) {
        if(par[i][a] != par[i][b]) {
            a = par[i][a];
            b = par[i][b];
        }
    }
    return par[0][a];
}

int dist(int u, int v) {
    return dep[u] + dep[v] - 2 * dep[ lca(u, v) ];
}

int ans;
void dfs1(int u, int p) {
    ans = max(ans, dep[u]);
    for(int i = 0; i < adj1[u].size(); i++) {
        int v = adj1[u][i];
        if(v == p) continue;
        if(dist(v, u) >= 3) {
            printf("-1");
            exit(0);
        }
        if(dep[v] - dep[Y] < 2 && tin[Y] <= tin[v] && tin[v] < tout[Y]) continue;
        dfs1(v, u);
    }
}

