    for(int i = 0; i < N - 1; i++) {
        int a, b; scanf("%d %d", &a, &b);
        a--; b--;

        adj1[a].push_back(b);
        adj1[b].push_back(a);
    }
    for(int i = 0; i < N - 1; i++) {
        int a, b; scanf("%d %d", &a, &b);
        a--; b--;

        adj2[a].push_back(b);
        adj2[b].push_back(a);
    }

    dfs0(Y, -1);

    while(dep[X] - dep[Y] > 2) {
        int mn = 1e9, p = -1;
        for(int i = 0; i < adj1[X].size(); i++) {
            int v = adj1[X][i];

            if(dist(X, v) >= 3) {
                printf("-1");
                return 0;
            }

            if(mn > dep[v]) {
                mn = dep[v];
                p = v;
            }
        }
        if(mn > dep[X]) break;
        X = p;

        for(int i = 0; i < adj2[Y].size(); i++) {
            int v = adj2[Y][i];
            if(tin[v] <= tin[X] && tin[X] < tout[v]) {
                Y = v;
                break;
            }
        }
    }

    dfs1(X, -1);

    printf("%d", 2 * ans);
}

