const int upTo = 201*1000;
const int lg = 18;
int nbNode;
int origRed, origBlue;
vector<int> adjRed[upTo];
vector<int> adjBlue[upTo];

int depth[upTo];
int anc[lg][upTo];

void dfs(int node, int parent) {
	anc[0][node] = parent;
	for (int i = 1; i < lg; ++i) {
		int tmp = anc[i-1][node];
		anc[i][node] = (tmp == -1 ? -1 : anc[i-1][tmp]);
	}
	for (int nei : adjBlue[node]) if (nei != parent) {
		depth[nei] = depth[node] + 1;
		dfs(nei, node);
	}
}

int dist(int u, int v) {
	int res = depth[u]+depth[v];
	if (depth[u] < depth[v]) swap(u,v);
	for (int i = lg-1; i >= 0; --i) {
		if (depth[u]-depth[v] >= (1<<i)) {
			u = anc[i][u];
		}	
	}
	if (u == v) {
		return res - 2*depth[v];
	}
	for (int i = lg-1; i >= 0; --i) {
		int nu = anc[i][u], nv = anc[i][v];
		if (min(nu,nv) != -1 && nu != nv) {
			u = nu; v = nv;
		}
	}
	int lca = anc[0][u];
	return res - 2*depth[lca];
}

int res = 0;
void esc(int node, int parent, int tps) {
	if (depth[node] <= tps) return;
	chmax(res, depth[node]);
	for (int nei : adjRed[node]) if (nei != parent) {
		int d = dist(node, nei);
		if (d >= 3) res = 3*upTo;
		esc(nei, node, tps+1);
	}
}

int main() {
	ios::sync_with_stdio(false);
	cin.tie(0);
	cin >> nbNode;
	cin >> origRed >> origBlue;
	--origRed; --origBlue;
	for (int i = 0; i < 2; ++i) {
		auto adj = (i ? adjBlue : adjRed);
		for (int iEdge = 0; iEdge < nbNode-1; ++iEdge) {
			int u, v;
			cin >> u >> v;
			--u; --v;
			adj[u].push_back(v);
			adj[v].push_back(u);
		}
	}	
	dfs(origBlue, -1); 
	esc(origRed, -1, 0);
	res *= 2;
	if (res == 6*upTo) res = -1;
	cout << res << "\n";
}

